generated.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

img {
    max-width: 100%;
    min-width: 100%;
}

body {
    background-color: #EEE;
    font-family: "inter";
    overflow: hidden;
    display: flex;
    justify-content: center;
}

input {
    font-family: "inter";
    font-size: 14px;
    border: 1px solid #DDD;
}

.site-nav>.nav-form {
    display: flex;
    justify-content: space-between;
}

ul,
ol,
ul li,
ol li {
    list-style: none;
}

.ui-loader-default {
    display: none
}

.ui-mobile-viewport {
    border: none;
}

.ui-page {
    padding: 0;
    margin: 0;
    outline: 0
}


li .outWrapper {
    width: 100%;
    height: 100%;
    text-decoration: none;
    display: flex;
    justify-content: center;
    color: inherit;
    position: relative;
}

li .outWrapper .icon-close-wrapper {
    position: absolute;
    right: 8%;
    top: 5%;
    display: none;
}

li:hover .icon-close-wrapper {
    display: block;
    animation: showCloseBtn 0.4s forwards;
}

@keyframes showCloseBtn {
    from {}

    to {
        background-color: #CCC;
    }
}

.guide {
    display: flex;
    margin: 30px 25% 10px 25%;
    justify-content: center;
    font-size: 12px;
}

.searchText {
    width: 100%;
    border-radius: 4px;
}

.searchButton {
    white-space: nowrap;
    border: none;
    background-color: #0282B9;
    border-radius: 4px;
    color: white;
}

.searchButton:hover {
    cursor: pointer;
}

.site-main {
    margin: 0 auto;
}

.siteList {
    display: flex;
    flex-flow: row wrap;
    justify-content: flex-start;
}

.siteList li {
    background-color: #fff;
    border: 1px solid #DDD;
    border-radius: 4px;
    display: flex;
    margin-right: 19px;
    justify-content: center;
    align-content: center;
    flex-wrap: wrap;
    flex-direction: column;
}

.siteList li:hover {
    cursor: pointer;
}

.site-info .addBtnWrapper {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.site-info .logo {
    display: flex;
    justify-content: center;
    width: 100%;
    height: 72px;
    font-size: 64px;
    margin: 15px 0px;
}

.site-info .link {
    width: 100%;
    display: flex;
    justify-content: center;
}

@media (max-width:500px) {

    .site-nav>.nav-form {
        margin: 60px 30px 20px 30px;
    }

    .searchText {
        width: 100%;
        margin-right: 10px;
        height: 40px;
        padding-left: 10px;
        border-radius: 4px;
        line-height: 40px;
    }

    .searchButton {
        width: 80px;
        font-style: 16px;
        padding: 0 10px;
        border-radius: 4px;
        color: white;
    }

    .guide {
        max-width: 100%;
        margin: 20px 10% 10px 10%;
    }

    .siteList {
        margin: 30px 15px 20px 30px;
        justify-content: space-between;
    }

    .siteList li {
        width: 150px;
        background-color: #fff;
        border-radius: 4px;
        margin-bottom: 20px;
        margin-right: 15px;
        font-size: 14px;
    }

    .siteList li:hover {
        cursor: pointer;
    }

    .site-info .link {
        width: 100%;
        height: 20px;
        margin-bottom: 15px;
    }

    .addButton .logo {
        margin-top: 25px;
        margin-bottom: 5px;
    }
}


@media (min-width:501px) {

    .site-nav .nav-form {
        max-width: 1160px;
        margin: 100px 0% 40px 0%;
        justify-content: space-between;
    }

    .searchText {
        width: 85%;
        height: 40px;
        padding-left: 10px;
        border: 1px solid #DDD;
        border-radius: 4px;
        line-height: 40px;
    }

    .searchButton {
        width: 100px;
        padding: 0 10px;
        font-size: 18px;
        color: white;
    }

    .guide {
        margin: 20px 0% 10px 0%;
    }

    .siteList {
        max-width: 1200px;
        margin: 50px 0% 20px 0%;
    }

    .siteList li {
        width: 200px;
        background-color: #fff;
        border-radius: 4px;
        margin-right: 40px;
        margin-bottom: 20px;
        font-size: 16px;
    }

    .site-info .link {
        width: 100%;
        height: 20px;
        margin-bottom: 15px;
    }

    .addButton .logo {
        margin-top: 25px;
        margin-bottom: 5px;
    }
}
